import React, { useState, useEffect } from 'react';
import { Layout, Card, Radio, Button, Typography, Result, RadioChangeEvent } from 'antd';
import 'antd/dist/reset.css';
import './App.css';
import QuizResult from './Quiz/Components/QuizResult';
import { fetchQuestions } from './Adapter/FetchQuestions';

const { Header, Content, Footer } = Layout;
const { Title } = Typography;
interface Question {
  id: number;
  question: string;
  options: string[];
  correctAnswer: string;
}
const questions : Question[] = [
  {
    id: 1,
    question: 'What is the capital of France?',
    options: ['Berlin', 'Paris', 'Rome', 'London'],
    correctAnswer: 'Paris',
  },
  {
    id: 2,
    question: 'What is the largest planet in our solar system?',
    options: ['Jupiter', 'Venus', 'Saturn', 'Mars'],
    correctAnswer: 'Jupiter',
  },
  {
    id: 3,
    question: 'What is the largest planet in our solar system?',
    options: ['Jupiter', 'Venus', 'Saturn', 'Mars'],
    correctAnswer: 'Jupiter',
  },
  // Add more questions here
];

const App = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState<string | null>(null);
  const [score, setScore] = useState(0);
  const [result, setResult] = useState(false);
  const [timer, setTimer] = useState<number>(10); // 60 seconds (1 minute)

  const currentQuestion = questions[currentQuestionIndex];
useEffect(() => {
const data = fetchQuestions();
console.log(data, "data");
}, [])
  useEffect(() => {
    let timerId: any
    if(!result) {
       timerId = setInterval(() => {
      setTimer((prevTimer) => {
        if (prevTimer > 0) {
          return prevTimer - 1;
        } else {
          if (currentQuestionIndex !== (questions.length -1)) {
          handleNextQuestion();
          return 10; // Reset timer to 60 seconds for the next question
        }
        else {
          setResult(true)
          return 0;
        }
      }});
    }, 1000);}

    return () => clearInterval(timerId); // Clean up the timer on component unmount
  }, [currentQuestionIndex]);

  const handleAnswerSelect = (e: RadioChangeEvent) => {
    setSelectedAnswer(e.target.value);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === currentQuestion.correctAnswer) {
      setScore((prevScore) => prevScore + 1);
    }

    setSelectedAnswer(null);
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    setTimer(10); // Reset timer to 60 seconds for the next question
  };

  return (
    <Layout>
      <Header className="header">
        <Title level={2}>Quiz App</Title>
      </Header>
      <Content className="content">
        { !result && <> <Card title={`Question ${currentQuestionIndex + 1}`} className="quiz-card">
          <p>{currentQuestion.question}</p>
          <Radio.Group onChange={handleAnswerSelect} value={selectedAnswer}>
            {currentQuestion.options.map((option) => (
              <Radio key={option} value={option}>
                {option}
              </Radio>
            ))}
          </Radio.Group>
        </Card>
        <div className="timer">
          <Title level={4}>Time Remaining: {timer} seconds</Title>
        </div></>}
        {!result && (currentQuestionIndex < questions.length - 1 ? (
          <Button type="primary" onClick={handleNextQuestion}>
            Next Question
          </Button>
        ) :   <Button type="primary" onClick={() => setResult(true)}>
        Finish
      </Button>)}
        {result && (
     <QuizResult score={score}/>
        )}
      </Content>
      <Footer className="footer">Quiz App Â©2023 Created by You</Footer>
    </Layout>
  );
};

export default App;
